mapper.select:
select o.id,o.busniss_code businessCode,o.product_code productCode,o.arrive_type arriveType,o.stock_state stockState,
o.charge_type chargeType,o.down_order_no downOrderNo,o.order_no orderNo,o.up_order_no upOrderNo,
o.agt_phone agtPhone,o.agt_no agtNo,o.charge_addr chargeAddr,o.charge_money chargeMoney,
o.profit,o.price,o.amount,o.in_money inMoney,o.out_money outMoney,o.apply_time applyTime,o.check_time checkTime,
o.state,o.check_state checkState,o.error_code errorCode,o.up_chanel upChanel,o.remark,o.balance,
o.account_balance accountBalance,o.exp1 exp1,o.exp2 exp2,o.exp3 exp3,
'支付' businessName,'ZFB' upChanelName
from bc_order_charge o
where agt_phone = #{agtPhone}

    and charge_addr =#{chargeAddr}


    and charge_money =#{chargeMoney}


    and down_order_no = #{downOrderNo}

order by id desc

mapper.update:
update
bc_order_nofity set state = #{state} where agt_phone = #{agtPhone} and
req_stream_id = #{reqStreamId}

update
bc_order_nofity set flag = #{flag},state = #{state}

    ,count = count + #{count}

where
agt_phone = #{agtPhone} and
req_stream_id = #{reqStreamId}

mapper.select:
select id, agt_phone agtPhone, req_stream_id reqStreamId, order_no orderNo, notify_url notifyUrl, charge_money price, profit, count, state, flag, date_format(apply_time,'%Y-%m-%d %H:%i:%s') applyTime from bc_order_nofity where 1=1
and id=#{id}
and agt_phone = #{agtPhone}
and req_stream_id = #{reqStreamId}
and order_no = #{orderNo}

select o.charge_addr accountNo,bbi.bank_account accountName,o.apply_time applyTime, o.charge_money chargeMoney,bon.pay_account_name payAccountName,bbi.bank_name bankName,bbi.bank_mark bankMark from bc_order_charge o left join bc_bank_info bbi on o.charge_addr = bbi.card_no and bbi.status = 0 left join bc_order_nofity bon on o.order_no = bon.order_no where 1=1 and o.state in (-1,2,5)
and o.order_no = #{orderNo}
and o.down_order_no = #{reqStreamId}

mapper.update:
update bc_order_charge
set state = #{state},
remark = #{remark},
check_state = #{checkState},
account_balance = #{accountBalance},
check_time = now(),
error_code = #{errorCode}

    ,up_order_no = #{upOrderNo}


    ,charge_addr = #{chargeAddr}


    ,exp3=1


    ,charge_type = #{chargeType}

where id = #{id}

    and ifnull(exp3,0)=0

update bc_order_charge
set state = #{state},
remark= #{remark},
check_time=now(),
check_state=1

    , charge_addr= #{chargeAddr}


    , exp2= #{exp2}


    , up_order_no= #{upOrderNo}


    ,exp3=1

where state in (-1,2,5,6)
and order_no = #{orderNo}

    and ifnull(exp3,0)=0

update bc_order_nofity set pay_account_name = #{exp2}

    ,charge_money = #{chargeMoney}

where order_no = #{orderNo}
and state!=0 and flag != 2

update bc_order_charge
set state = #{state},
check_state = #{checkState},
remark= #{remark},
check_time=now()

    , up_order_no= #{upOrderNo}


    , charge_addr= #{chargeAddr}


    , exp2= #{exp2}


    ,exp3=1

where state in (-1,2)
and order_no = #{orderNo}

    and ifnull(exp3,0)=0

update
bc_order_charge
set
channel_price =#{channelPrice},
up_chanel=#{upchanel},
error_code=#{errorCode},
remark=#{remark}

    , up_order_no= #{upOrderNo}

where order_no = #{orderNo}
