mapper.select:
select p.product_num productCode,p.business_num businessCode,p.number_id arriveType,p.product_type productType, p.trade_face tradeFace, p.price,p.special_type specialType,pg.discount from lfs_userrelationptgroup ug,lfs_productrelationgroup pg,lfs_product p ,lfs_productgroup g where ug.user_num = #{agtNo} AND ug.group_num = pg.group_num AND pg.product_num = p.product_num AND g.group_num = ug.group_num AND p.state = 1 AND g.group_state = 1 AND pg.product_state = 1
and pg.product_num = #{productCode}
AND p.product_type = #{productType}
and p.business_num = #{businessCode}
and p.trade_face = #{chargeMoney}
and p.number_id=#{chargeType}
order by pg.discount desc limit 1

select p.product_num productCode,p.business_num businessCode, p.trade_face tradeFace, p.price,pg.discount from lfs_userrelationptgroup ug,lfs_productrelationgroup pg,lfs_product p,lfs_productgroup g where ug.user_num=#{agtNo}
and p.business_num = #{businessType}
and ug.group_num=pg.group_num and pg.product_num = p.product_num and g.group_num = ug.group_num and p.state=1 and g.group_state=1 and pg.product_state = 1

SELECT tg.channel_num, tg.product_num, tg.end_price, tg.channel_price, app.app_name, bs.weight, info.adapter_name FROM lfs_channel_to_product tg LEFT JOIN lfs_channel_to_group bs ON tg.channel_num = bs.channel_num LEFT JOIN lfs_channel_info info ON bs.channel_num = info.channel_num LEFT JOIN lfs_app_info app ON app.group_channel_id = bs.channel_group_id LEFT JOIN lfs_product p ON tg.product_num = p.product_num WHERE app.agt_no = #{agtPhone} AND info.channel_st = 1 AND tg.state = 1 AND p.state = 1 AND bs.weight > 0
AND p.product_type = #{productType}
AND p.trade_face = #{chargeMoney}
and p.business_num = #{businessCode}
ORDER BY end_price ASC
